import React, { useState } from 'react';
import './App.css';

function App() {
  const [arrNums, setArrNums] = useState([9, 1, 3, 2, 5, 6, 9, 4, 5, 7, 8]);
  const [newArrNums, setNewArrNums] = useState([]);

  const bubbleSort = () => {
    const data = [...arrNums]; // Create a copy of the array to avoid mutating the state directly
    for (let i = 0; i < data.length - 1; i++) {
      for (let j = 0; j < data.length - i - 1; j++) {
        if (data[j] > data[j + 1]) {
          // Swap elements if they are in the wrong order
          const temp = data[j];
          data[j] = data[j + 1];
          data[j + 1] = temp;
        }
      }
    }
    setNewArrNums(data);
  };

  return (
    <div>
      <h3>Unsorted Data</h3>
      <ul className="numbers">
        {arrNums.map((num, index) => (
          <li className="items" key={index}>
            {num}
          </li>
        ))}
      </ul>

      <h3>1. Bubble Sort</h3>
      <button onClick={bubbleSort}>Sort Bubble Sort</button>
      <ul className="numbers">
        {newArrNums.map((num, index) => (
          <li className="items" key={index}>
            {num}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
